<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Document</title>

    <link rel="stylesheet" href="css/showQR.css" />
</head>

<body>
    <div class="footer">
        <button id="showQRBtn" type="submit">QR Code</button>
    </div>


    <!-- showQR Popup -->
    <div id="showQRPopup" class="popup">
        <div class="popup-content">
            <span class="close">&times;</span>
            <!-- ดึงข้อมูลรูป QR code -->
            <div class="flex-container">
                <div class="flex-item">
                    <h3>Tester</h3>
                    <img class="qr-img"
                        src="data:image/png;base64,"
                        alt="">
                    <img class="download" src="https://cdn-icons-png.flaticon.com/128/2989/2989976.png" alt="">
                </div>
                <div class="flex-item">
                    <h3>Tester</h3>
                    <img class="qr-img"
                        src="data:image/png;base64,"
                        alt="">
                        <img class="download" src="https://cdn-icons-png.flaticon.com/128/2989/2989976.png" alt="">
                </div>
                <div class="flex-item">
                    <h3>Tester</h3>
                        <img class="qr-img"
                            src="data:image/png;base64,"
                            alt="">
                            <img class="download" src="https://cdn-icons-png.flaticon.com/128/2989/2989976.png" alt="">
                </div>
                <!-- เพิ่ม items ตามต้องการ -->
            </div>
            <!-- ดึงข้อมูล comment มาแสดง -->
            <div class="comment">
                <h3>Comment : data</h3>
            </div>
        </div>
    </div>


    {{-- pop up --}}
    <script>
        const showQRBtn = document.getElementById('showQRBtn');
        const showQRPopup = document.getElementById('showQRPopup');
        const closeBtn = document.getElementsByClassName('close')[0];
        const applyShowQRBtn = document.getElementById('applyshowQR');


        // Open showQR popup
        showQRBtn.onclick = function() {
            showQRPopup.style.display = 'block';
        }

        // Close showQR popup
        closeBtn.onclick = function() {
            showQRPopup.style.display = 'none';
        }

        // Close popup when clicked outside
        window.onclick = function(event) {
            if (event.target == showQRPopup) {
                showQRPopup.style.display = 'none';
            }
        }

        // Apply showQR
        applyShowQRBtn.onclick = function() {


            // Close popup after applying showQR
            showQRPopup.style.display = 'none';
        }

        cancelShowQRBtn.onclick = function() {
            // Reset showQR form values if needed
            // ...

            // Close popup without applying showQR
            showQRPopup.style.display = 'none';
        }
    </script>
</body>
